import { useEffect, useState } from 'react';
import { Task } from '../interfaces';
import { useAppSelector } from '../store/hooks';

const useSearchQuery = (searchQuery: string) => {
  const tasks = useAppSelector((state) => state.tasks.tasks);

  const [matchedTasks, setMatchedTasks] = useState<Task[]>([]);

  useEffect(() => {
    let searchByStatus;
    if(searchQuery.toLocaleLowerCase() === 'in progress') {
      searchByStatus = false;
    } else if (searchQuery.toLocaleLowerCase() === 'done') {
      searchByStatus = true;
    }
    const filteredTasks = tasks.filter((task: Task) => {
      return task.title.toLowerCase().includes(searchQuery.toLowerCase()) || task.status === searchByStatus;
    });
    if (searchQuery.trim().length) {
      setMatchedTasks(filteredTasks);
    } else {
      setMatchedTasks([]);
    }
  }, [searchQuery, tasks]);

  return matchedTasks;
};

export default useSearchQuery;
